definition.script:
pipeline {
    agent any

    environment {
        PATH = "${tool('nodejs21')}/bin:$PATH"
    }

    stages {
        stage('Git Checkout') {
            steps {
                git url: 'https://github.com/yashaswi29/CICD-Realtime-ChatApp.git', branch: 'master'
            }
        }
        stage('Install Dependencies') {
            steps {
                sh 'npm install'
            }
        }
        stage('Start Server') {
            steps {
                sh 'node server.js'
            }
        }
        stage('hello') {
            steps {
                sh 'echo hello world'
            }
        }
    }
}
